% sine800hzsamp.m: simulated sampling of the 800 Hz sine wavef=800; time=0.007; Tu=1/80000; t=Tu:Tu:time;    % freq and time vectors w=sin(2*pi*f*t+0.3);                            % create sine wave w(t)M=10;                                           % take 1 in M sampleswk=w(1:M:end);                                  % the "sampled" sequencews=zeros(size(w)); ws(1:M:end)=wk;              % sampled waveform ws(t)subplot(211), plot(t,w)                         % plot the waveformhold on, plot(t,ws,'r'), hold off               % plot "sampled" wavexlabel('segundos'), ylabel('amplitud')          % label the axessubplot(212), plot(t(1:M:end),wk,'ro')          % plot samplesxlabel('segundos'), ylabel('amplitud')          % label the axes